inline_styles:
body {
    background:#f3f3f2;
}
.Text{
    color: #000000;
}
.Link {
    color: #ff5317;
}
.Text_color_ghost {
    color: rgba(0, 0, 0, 0.45);
}
.Text_weight_regular {
    font-size: 16px;
}
@media only screen and (max-width: 650px) {
    .Container {
        padding: 48px 24px;
    }
}
@media only screen and (min-width: 650px) {
    .Container {
        max-width: 420px;
        width: auto;
        padding: 48px;
    }
}
.Link_view_captcha {
    display: none;
}
.CheckboxCaptcha-Label .CheckboxCaptcha-LabelText {
    font-size: 22px;
    line-height: 26px;
    font-weight: 500;
}
.CheckboxCaptcha-LabelText:first-letter {
    color: initial;
}
.CheckboxCaptcha {
    background: #f3f3f2;
}
.CheckboxCaptcha:hover {
    background: #e6e6e6;
}
.CheckboxCaptcha-Anchor{
    background-color: initial;
}
.CheckboxCaptcha-Button, .CheckboxCaptcha, .CheckboxCaptcha:after {
    border-radius: 12px;
    border: none;
}
button[type="submit"]:before {
    background-color: rgba(0, 0, 0, 0.06);
}
.CheckboxCaptcha-Label {
    margin-bottom: 7px;
}
.CheckboxCaptcha-Links {
    display: none;
}

